package mytraining;

public class Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		//signed value + and - (both)  2n-1
		
		//unsigned value  + only postive only
		
		
		byte b=Byte.MIN_VALUE;
		
		byte b2=Byte.MAX_VALUE;
		
		
		System.out.println("The byte min value"+b);
		
		System.out.println("The byte max value"+b2);
		
		
		short sh=Short.MIN_VALUE;
		
		short sho=Short.MAX_VALUE;

		
        System.out.println("The short min value"+sh);
		
		System.out.println("The short max value"+sho);

		

		int i=Integer.MIN_VALUE;
		
		int in=Integer.MAX_VALUE;

		
        System.out.println("The i min value"+i);
		
		System.out.println("The  in  max value"+in);


		
		

		long lo=Long.MIN_VALUE;
		
		long lon=Long.MAX_VALUE;

		
        System.out.println("The long min value"+lo);
		
		System.out.println("The long max value"+lon);

		
		
		
		
		


	}

}
